        <button 
          onClick={() => window.location.href = '/'}
          className="bg-white text-dentor-empati-start font-nunito font-bold py-3 px-6 rounded-lg hover:bg-gray-100 transition-colors"
        >
          Ana Sayfaya DÃ¶n
        </button>
      </div>
    </div>
  );
};

export default App;
